mapper.update:
UPDATE tbl_reply
replytext=#{replyText}, updateDate=#{updateDate},
WHERE rno=#{rno};

mapper.select:
select rno, bno, replytext, replyer, regdate, updatedate 
  from tbl_reply
  where bno = #{bno}
  order by rno desc
  limit #{cri.pageStart}, #{cri.perPageNum}

select count(bno) 
  from tbl_reply
 where bno = #{bno}
